const longestPalindrome = require('./5-palindromic-substring');

describe('longest palindrome', () => {
  it('example 1', () => {
    expect(longestPalindrome('babad')).toBe('bab');
  });

  it('example 2', () => {
    expect(longestPalindrome('cbbd')).toBe('bb');
  });

  it('no palindromes', () => {
    expect(longestPalindrome('abc')).toBe('a');
  });

  it('long', () => {
    const input = '';
    expect(longestPalindrome(input)).toBe('');
  })
});
